# -*- coding: utf-8 -*-from models.supplier import Supplierfrom application import dbclass suppliermigrate:    def run(self):        db.engine.execute("truncate table supplier")        tmplist=[            {'name':'戴尔','short_name':'DELL'},            {'name':'思科','short_name':'CISCO'},            {'name':'华三通信','short_name':'H3C'},            {'name':'惠普','short_name':'HP'},            {'name':'瞻博网络','short_name':'juniper'},            {'name':'华为','short_name':'华为'}        ]        for item in tmplist:            tmpinfo=Supplier.query.filter(Supplier.name==item['name']).first()            if tmpinfo:                continue            target=Supplier(item['name'],short_name='',content=item['short_name'])            db.session.add(target)            db.session.commit()